	if( FIRST_component(la.d_type) ) {
		component(st);
		while( la.d_type == Tok_Comma ) {
			if( expect(Tok_Comma, false, "constructor") ) addTerminal(st);
			component(st);
		}
	}
	if( expect(Tok_Rbrace, false, "constructor") ) addTerminal(st);
